import React, { useEffect } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import axios from 'axios';

const useStyles = makeStyles((theme) => ({
  container: {
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

const getFilters = () => {
  return new Promise(async (resolve, reject) => {
    try {
      const html = await axios.get(
        `http://www.mocky.io/v2/5a25fade2e0000213aa90776`,
      );
      resolve(html.data);
    } catch (error) {
      reject(error);
    }
  });
};

function Filters() {
  const classes = useStyles();
  const [listFilters, setListFilters] = React.useState(null);
  useEffect(() => {
    const getInitialData = async () => {
      const responseFilters = await getFilters();
      setListFilters(responseFilters.filters);
    };
    getInitialData();
  }, []);

  const [filters, setFilters] = React.useState({
    locale: '',
    country: '',
    timestamp: '',
    limit: '',
    offset: '',
  });

  const handleChange = (event) => {
    console.log(event.target.value);
    // setFilters(event.target.value);
  };

  return (
    <div className={classes.container}>
      {listFilters &&
        listFilters.filter(item => item.values && item.values.length).map((item) => (
          <>
            <FormControl className={classes.formControl}>
              <InputLabel id="demo-simple-select-label">{item.name}</InputLabel>
              <Select
                labelId="demo-simple-select-label"
                id="demo-simple-select"
                // value={age}
                onChange={handleChange}
              >
                {item.values.map((item) => (
                  <MenuItem value={item.value}>{item.name}</MenuItem>
                ))}
              </Select>
            </FormControl>
          </>
        ))}
    </div>
  );
}

export default Filters;
